let alertButton=document.getElementById("alert-button"),resetButton=document.getElementById("reset-button"),alertWindow=document.getElementById("alert"),overlay=document.getElementById("overlay"),xIcon=document.getElementById("x-icon"),counter=document.getElementById("counter"),buttonClickCounter=localStorage.getItem("counter");function checkButton(t){return counter.textContent=t+" times",localStorage.setItem("counter",t),(5<t?show:hide)(resetButton)}function hide(t){t.classList.remove("show"),setTimeout(()=>{t.classList.add("hide")},300)}function show(t){t.classList.remove("hide"),setTimeout(()=>{t.classList.add("show")},1)}alertButton.addEventListener("click",()=>{show(overlay),checkButton(++buttonClickCounter)}),resetButton.addEventListener("click",()=>{checkButton(buttonClickCounter=0)}),xIcon.addEventListener("click",()=>{hide(overlay)}),document.addEventListener("mouseup",t=>{overlay.classList.contains("hide")||alertWindow.contains(t.target)||hide(overlay)});